import React,{ useState } from 'react'
import './cart.css';
import Header from  './Header';
import Head from './Head';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import { useSelector } from 'react-redux';
import { selectGmail } from '../features/userSlice';
import styled from 'styled-components';

function Cart() {
    let gmail = useSelector(selectGmail);
    const [buyit, setBuyit] = useState(false);
  
    const buyitChange = () =>{
        setBuyit(true);
    }
    const savedit= () =>{
        setBuyit(false);
    }
    var settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 4,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              initialSlide: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
      };
    return (
        <Container>
            <Header />
            <Head/>
              {!gmail && (
                    <Content>
                        <AmazonCart>
                            <AmazonCartContent>
                                <ContentImg src="https://m.media-amazon.com/images/G/01/cart/empty/animated/cart-fallback-desaturated._CB405682035_.svg" alt="">
                                    
                                </ContentImg>
                            </AmazonCartContent>
                        </AmazonCart>
                        <AmazonDiv>cart</AmazonDiv>
                    </Content>
             
                  
              )}
              </Container>
    )
}

export default Cart


const Container = styled.div`

  background: rgb(236,231,238);
  width:100%;
  height: 100vh;
`
const AmazonCart = styled.div`
     margin-top: 30px;
     background-color: white;
     margin-left: 10px;
     margin-right: 10%;
     height: 200px;
     
`
const AmazonDiv = styled.div`
    background-color: white;
    margin-top:10px;
`
const Content = styled.div``

const AmazonCartContent = styled.div`
    padding: 20px;
`


const ContentImg = styled.img`
    height: 140px;
`
