import React, { useState, useEffect } from 'react';
import './Feed.css';
import CreateIcon from '@material-ui/icons/Create';
import InputOption from './InputOption';
import ImageIcon from '@material-ui/icons/Image';
import SubscriptionsIcon from '@material-ui/icons/Subscriptions';
import EventNoteIcon from '@material-ui/icons/EventNote';
import CalendarViewDayIcon from '@material-ui/icons/CalendarViewDay';
import Post from './Post';
import { db } from './firebase';
import firebase from 'firebase';

function Feed() {
     const [posts, setPosts] = useState([]);
     const [input, setInput] = useState([]);
//   real time listener to database 
     useEffect(() => {
         db.collection("posts").onSnapshot(snapshot =>{
            setPosts(snapshot.docs.map((doc) =>(
                {
                   id: doc.id,
                   data:doc.data()
                }
            )
            ))
         }
         )
         
     }, [])


     const sendPost = e =>{
     e.preventDefault();
     db.collection(posts).add(
         {
             name:'El Ayadi Abderrahim',
             descrption:"this is a test",
             message: input,
             photoUrl: '',
             timestamp: firebase.firestore.FieldValue.serverTimestamp()
         }
     )   
     }

    return (
        <div className='feed'>
            <div className='feed__inputContainer'>
             <div className='feed__input'>
              <CreateIcon/>
              <form>
                <input value={input} onChange={e => setInput(e.target.value)} type="text"></input>
                <button onClick={sendPost} type='submit'>Send</button>
              </form>
             </div>
             <div className='feed__inputOptions'>
                <InputOption Icon={ImageIcon} title='Photo' color="#7085F9"/>
                <InputOption Icon={SubscriptionsIcon} title='Video' color="#E7A33E"/>
                <InputOption Icon={EventNoteIcon} title='Event' color="#C0CBCD"/>
                <InputOption Icon={CalendarViewDayIcon} title='Write article' color="#7FC15E"/>

             </div>
            </div>
            {/* Posts */}
            {posts.map(({id, data: {name, descrption, message, photoUrl}}) => {
                <Post
                key= {id}
                name = {name}
                description={descrption}
                message = {message}
                photoUrl = {photoUrl}
                />
            })}
            <Post name="Abderrahim El Ayadi"
                  descrption="This is a test"
                  message="WOW look it is worked"
                />
        </div>
    )
}

export default Feed
