import { createSlice } from '@reduxjs/toolkit'

const initialState = {
       gmail: null,
       password: null
}

const signInSlice = createSlice({
    name: "gmail",
    initialState,
    reducers: {
             setGmail: (state, action) =>{
                 state.gmail = action.payload.gmail
                 console.log(state.gmail)
             }
    }
});
export const selectGmail = ( state ) =>(state.gmail.gmail)
export const { setGmail } = signInSlice.actions
export default signInSlice.reducer
