import { createSlice } from '@reduxjs/toolkit'

const initialState = {
       gmail: null,
       password: null
}

const signInSlice = createSlice({
    name: "infoUser",
    initialState,
    reducers: {
             setGmail: (state, action) =>{
                 state.gmail = action.payload.gmail
             }
    }
});
export const selectGmail = ( state ) =>(state.infoUser.gmail)
export const { setGmail } = signInSlice.actions
export default signInSlice.reducer
